let initialState = {users:[]}

let ContactReducer =(state=initialState,action)=>{
  switch (action.type) {
    case "ADD_CONTACT":
        return{...state, users:[...state.users, action.payload]};

    default:
        return state
}
}

export default ContactReducer;
