import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import { Col, Container, Row } from 'react-bootstrap'
import  Contact from "./components/Contact";
import ContactForm from './components/ContactForm';

function App() {
  const [users, setUsers] = useState([
    { 
      name: "Kelvin ", 
      phonenumber: " +233 45(345)-3454",
      location: "East-Legon", id: "17373788" 
    },
    { 
      name: "Dela",
      phonenumber: " +44 (345)564-7439 ",
      location: "Brimingham ",
      id: "19929920" },
    { 
      name: " Orison",
      phonenumber: "+1 (546)725-0289",
      location: "College-Park ",
      id: "uqawquei" },
  ]);


const AddNewUser = (data) => {
  data.id = Math.random();toString();
  setUsers([...users,data])
  console.log(AddNewUser);
}

const EditUser = (id,newData) => {
setUsers(
  users.map((wantedUser)=> {
    if (wantedUser.id === id) {
      return newData;
    }
    return wantedUser;
  })
)
}




const DeleteUser = (id) => {
setUsers(
  users.filter((user)=> {
    if (user.id !== id) {
      return user
    }
  })
)

}


return (
  <>
<Container>
 <Row>
 <Col>
 <ContactForm addUser= {AddNewUser}/>
 </Col>


<Col>
<Contact user={users} editUser={EditUser} delete={DeleteUser}/>
</Col>

 </Row>
</Container>
  </>
 );
}

export default App;
