let initialState = {contacts:[]}

let ContactReducer =(state=initialState,action)=>{
  switch (action.type) {
    case "ADD_CONTACT":
        return{...state, contacts:[...state.contacts, action.payload]};

        case "DELETE_CONTACT":
          const filteredContact = state.users.filter(user=> user.id !== action.payload)
          return{...state, users:filteredContact} 

      case "EDIT_CONTACT": 
          const updatedContact = state.users.map((user)=>{
              if(user.id===action.payload.data.id) {
                  return action.payload.data
              }
              else {
                  return user
              }
          })


          return {...state,users:updatedContact}

      default:
          return state
}
  }


export default ContactReducer;
